import './card.css'
const Card = (props) => {

   
    
    return (
        <div className='tc bg-light-green dib br3 pa3 ma2 grow bw2 shadow-5'>
            <img style={{height :200 , width : 200}} alt='photo' src={Scr1} />
            <div>
                <a href="#" className={props.name.length <= 17 ? 'smallname' : 'bigname'}>{props.name.slice(0,23)}</a><br />
                <a href="#" className={props.name.length <= 17 ? 'smallname' : 'bigname'}>{props.name.slice(23,46)} </a><br />
                <a href="#" className={props.name.length <= 17 ? 'smallname' : 'bigname'}>{props.name.slice(46,69)} </a>
                <p>{props.email}</p>
            </div>
        </div>
    );
}

