import React, { Component } from 'react';
import {robots}  from './robots';
import CardList from './CardList';

class Blognav extends React.Component {
    

    state = {
        blogs : robots 
    }
    

    alllShow = () => {
        let newblogs = this.state.blogs
        
        this.setState({blogs : newblogs})
    }


    mlShow = () => {
        let newblogs = this.state.blogs
        newblogs = newblogs.filter( i => i.imag==='ML')
        this.setState({blogs : newblogs})
    }

    iotShow = () => {
        let newblogs = this.state.blogs
        newblogs = newblogs.filter( i => i.imag==='IOT')
        this.setState({blogs : newblogs})
    }

    webShow = () => {
        let newblogs = this.state.blogs
        newblogs = newblogs.filter( i => i.imag==='WEB')
        this.setState({blogs : newblogs})
    }

    render() { 
        return (
        <div>
            <span >
                 
                 <button onClick={this.alllShow} href="#">ALL</button> &nbsp;&nbsp;
                 <button onClick={this.mlShow} href="#">ML</button> &nbsp;&nbsp;
                 <button onClick={this.iotShow} href="#">IOT</button> &nbsp;&nbsp;
                 <button onClick={this.webShow} href="#">WEB</button> &nbsp;&nbsp;
                 <CardList robots={this.state.blogs} />
            </span>
        </div>
        );
    }
}
 
export default Blognav;
