import React, {Component} from "react";
import Cardlist from '../components/CardList';
import SearchBox from  '../components/SearchBox';
import Scroll from "../components/Scroll.js";
import { robots } from '../components/robots';


const state = {
    robots: robots,
    searchfield: ''
}

class  App extends Component{
constructor() {
    super()
    this.state ={
        robots: robots,
        searchfield: ''
    }
}

//there is another way to grab data of each robot that is using "fetch("website")" see the video "react app 4"


onSearchChange = (event) => {
    this.setState({searchfield: event.target.value})
    
    
}
    render() {
        const {robots, searchfield} = this.state; //destructing // now u can robots instead of this.state.robots  and searchfield instead of this.state.searchfield
        const filteredRobots = this.state.robots.filter(robots => {
            return robots.name.toLowerCase().includes(searchfield.toLowerCase());
        
        })
        return (
            <div className='tc'>
        <h1>Robofriends</h1>
        <SearchBox searchChange={this.onSearchChange}/>
        <Scroll>
        <Cardlist robots={filteredRobots}/>
        </Scroll>
        </div>
        )
    }
}


export default App



//state is an object that describes our application


//state >> props
