import React, {Component} from "react";
import Cardlist from './CardList';
import SearchBox from  './SearchBox';
import { robots } from './robots';


const state = {
    robots: robots,
    searchfield: ' '
}

class  App extends Component{
constructor() {
    super()
    this.state ={
        robots: robots,
    searchfield: ''
    }
}

    render() {
        return (
            <div className='tc'>
        <h1>Robofriends</h1>
        <SearchBox />
        <Cardlist robots={this.state.robots}/>
        </div>
        )
    }
}


export default App



//state is an object that describes our application


