import React, {Component} from "react";
import Cardlist from '../components/CardList';
import SearchBox from  '../components/SearchBox';
import Scroll from "../components/Scroll.js";
import { blogs } from '../components/blogs';


const state = {
    blogs: blogs,
    searchfield: ''
}

class  App extends Component{
constructor() {
    super()
    this.state ={
        blogs: blogs,
        searchfield: ''
    }
}

//there is another way to grab data of each robot that is using "fetch("website")" see the video "react app 4"


onSearchChange = (event) => {
    this.setState({searchfield: event.target.value})
    
    
}
    render() {
        const {blogs, searchfield} = this.state; //destructing // now u can blogs instead of this.state.blogs  and searchfield instead of this.state.searchfield
        const filteredblogs = this.state.blogs.filter(blogs => {
            return blogs.name.toLowerCase().includes(searchfield.toLowerCase());
        
        })
        return (
            <div className='tc'>
        
        <SearchBox searchChange={this.onSearchChange}/>
        
        <Cardlist blogs={filteredblogs}/>
        
        </div>
        )
    }
}


export default App



//state is an object that describes our application


